import React, { Component } from "react";

export default class Fullname extends Component {

    constructor() {
        super();
        this.state = {
            src: "https://www.coolshe.net/wp-content/uploads/2021/06/%D9%85%D9%88%D8%B9%D8%AF-%D8%A7%D9%86%D8%AA%D9%87%D8%A7%D8%A1-%D9%88%D9%86-%D8%A8%D9%8A%D8%B3.jpg",
            counter: 0

        }
    }







    increse = () => {
        this.setState({
            counter: this.state.counter + 1
        })
    }
    decrese = () => {
        this.setState({
            counter: this.state.counter - 1
        })
    }

    muftah = () => {
        this.setState({
            counter: 0
        })

    }

    decrese = () => {
        if (this.state.counter > 0) {
            this.setState({
                counter: this.state.counter - 1
            });
        }
    }


    render() {
        return (
            <div className="vv">
                <h3></h3>

                <p> {this.state.counter} </p>

                <img className="kk" src={this.state.src} alt="img in src" />

                <div className="mn">
                    <button onClick={this.increse}  >+</button>
                    <button onClick={this.decrese}>-</button>
                    <button onClick={this.muftah}>reset</button>
                </div>


            </div>
        )
    }






}
